
public class P2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int n=5;// number of rows
int k=n;
for(int i=1;i<=n;i++)  // iterate number of rows times
{
   
	for(int j=k;j>=1;j--)
	{
		System.out.print("*");
		
		
	}
	k--;
	
	System.out.println(); // for each new row
	
}
		
		
		
	}

}
